<?php\nnamespace Opencart\\Catalog\\Controller\\Product;\n\nclass NnlProducts extends \\Opencart\\System\\Engine\\Controller {\n    public function index(): void {\n        $this->load->language(\'product/category\');\n\n        $this->load->model(\'catalog/product\');\n\n        $this->document->setTitle(\$this->language->get(\'heading_title\'));\n\n        $data[\'breadcrumbs\'] = [];\n\n        $data[\'breadcrumbs\'][] = [\n            \'text\' => \$this->language->get(\'text_home\'),\n            \'href\' => \$this->url->link(\'common/home\')\n        ];\n\n        $data[\'breadcrumbs\'][] = [\n            \'text\' => \'NNL Products\',\n            \'href\' => \$this->url->link(\'product/nnl_products\')\n        ];\n\n        $data[\'heading_title\'] = \'NNL Products\';\n\n        $data[\'products\'] = [];\n\n        $products = \$this->model_catalog_product->getProducts();\n\n        foreach (\$products as \$product) {\n            $data[\'products\'][] = [\n                \'product_id\'  => \$product[\'product_id\'],\n                \'thumb\'       => \$this->model_tool_image->resize(\$product[\'image\'], \$this->config->get(\'config_image_product_width\'), \$this->config->get(\'config_image_product_height\')),\n                \'name\'        => \$product[\'name\'],\n                \'description\' => utf8_substr(strip_tags(html_entity_decode(\$product[\'description\'], ENT_QUOTES, \'UTF-8\')), 0, \$this->config->get(\'config_product_description_length\')) . \'..\',\n                \'price\'       => \$this->currency->format(\$this->tax->calculate(\$product[\'price\'], \$product[\'tax_class_id\'], \$this->config->get(\'config_tax\')), \$this->session->data[\'currency\']),\n                \'href\'        => \$this->url->link(\'product/product\', \'product_id=\' . \$product[\'product_id\'])\n            ];\n        }\n\n        \$data[\'column_left\'] = \$this->load->controller(\'common/column_left\');\n        \$data[\'column_right\'] = \$this->load->controller(\'common/column_right\');\n        \$data[\'content_top\'] = \$this->load->controller(\'common/content_top\');\n        \$data[\'content_bottom\'] = \$this->load->controller(\'common/content_bottom\');\n        \$data[\'footer\'] = \$this->load->controller(\'common/footer\');\n        \$data[\'header\'] = \$this->load->controller(\'common/header\');\n\n        \$this->response->setOutput(\$this->load->view(\'product/nnl_products\', \$data));\n    }\n}\n